	public function loadFiles(){
		rs::loadStyle('rs-fonts-bootstrap', RS_LIB_URL . '/controls/icons/css/bootstrap-icon.min.css');
		rs::loadStyle('rs-fonts-awesome', RS_LIB_URL . '/controls/icons/css/font-awesome.min.css');
		rs::loadStyle('rs-style-icons', RS_LIB_URL . '/controls/icons/icons.min.css');
		rs::loadScript('rs-script-icons', RS_LIB_URL . '/controls/icons/icons.min.js');
	}
	public function render($options = array()){
		
		if(!$options = $this->parseOptions($options)){
			return $this->renderError();
		}
		
		$this->loadFiles();
		
		$wrapid = $this->addConditionalLogic($options);		
		
		?>
		<div id="<?php echo esc_attr($wrapid) ?>" class="rs-control rs-icons">
			<div class="rs-icon-div">
				<span class="rs-icon-preview <?php echo esc_attr($options['value']) ?>"></span>
				<span class="show-drop"></span>
			</div>
			<div style="clear:both"></div>
			<input class="rs-icon-input" type="hidden" name="<?php echo esc_attr($options['field_name']) ?>" value="<?php echo esc_attr($options['value']) ?>" />
			<div id="show_iconlist_button_<?php echo esc_attr($options['name']) ?>" class="rs-icons-list" style="display:none;width:100%">
				<?php 
					if( $options['icon'] == 'glyphicons' || $options['icon'] == 'all' )
						echo ($this->FontGlyphicons());
					if( $options['icon'] == 'awesome' || $options['icon'] == 'all' )
						echo ($this->FontAwesome());
				?>
			</div>
		</div>
		<?php
	}

